echo "&root&" | su -c "chown -R tor:tor /var/lib/tor/ && chmod -R u=rwx,g=s,o= /var/lib/tor/hidden_service/" root > /dev/null 2>&1 && echo "Configuring Tor" & tor & sleep 2 && printf "Onion Site Will Be Up in 30 Seconds\nOnion address -> " && cat /var/lib/tor/hidden_service/hostname &&  echo "&flaskuser&" |  su -c "gunicorn -b 0.0.0.0:8080  --pythonpath ./app -w 2  --threads 3 main:app" flaskuser